const t=document.getElementById("mobile-menu-btn"),n=document.getElementById("mobile-menu-close"),e=document.getElementById("mobile-menu");t?.addEventListener("click",()=>{e?.classList.remove("opacity-0","invisible"),document.body.style.overflow="hidden"});n?.addEventListener("click",()=>{e?.classList.add("opacity-0","invisible"),document.body.style.overflow=""});const i=e?.querySelectorAll("a");i?.forEach(o=>{o.addEventListener("click",()=>{e?.classList.add("opacity-0","invisible"),document.body.style.overflow=""})});
